const array = msg.payload;

for (let index = 0; index < 20; index++) {
    msg.payload = x[index];
    node.send([[null], [msg]]); 
}
msg.payload = array[array.length - 1];
return [msg, null];

let inspect = flow.get("thInspect") || false;
let data = msg.data.sensor_data;
data.Timestamp = msg.data.received;
data.battery = Number(msg.data.battery_percent);
msg.payload = [data];
if(inspect){
    return [msg, msg];
}
return [msg, null];

let actualMAC = msg.payload;
let lastMAC = context.get("lastMAC") || false;

if (actualMAC != lastMAC)
{
    context.set("lastMAC", actualMAC);
    return msg;
}

let count = flow.get("thcountMAC");
let type = 0;
for (let index = 0; index < count; index++) {
    if (msg.options[index] == msg.payload)
    {
        type = flow.get("thMAC["+index+"]")[1];
    }
}
if(type == 4)
{
    flow.set("thtype", 4)
    flow.set("thflagType", true)
    return[msg, null, null];
}
if(type == 23)
{
    flow.set("thtype", 23)
    flow.set("thflagType", false)
    return[null, msg, null];
}
if (type == 12) {
    flow.set("thtype", 12)
    flow.set("thflagType", false)
    return [null, null, msg];
}
else
{
    node.error("Empty or incompatible ncd MAC device", msg);
}
